mapper.insert:
INSERT INTO CART(MEMBER_ID, PRODUCT_NO, QUANTITY) VALUES( #{memberId}, #{productNo},
${quantity} 1
)

mapper.delete:
DELETE CART
WHERE MEMBER_ID = #{memberId}
AND PRODUCT_NO = #{productNo}

mapper.update:
UPDATE CART
SET QUANTITY = #{quantity}
WHERE MEMBER_ID = #{memberId}
AND PRODUCT_NO = #{productNo}
