mapper.select:
SELECT COUNT(id)
FROM MEMBER
WHERE ID= #{id}

SELECT COUNT(email)
FROM MEMBER
WHERE EMAIL= #{email}

SELECT COUNT(phone)
FROM MEMBER
WHERE PHONE= #{phone}

SELECT COUNT(nickname)
FROM MEMBER
WHERE NICKNAME= #{nickname}

mapper.update:
	UPDATE INTEREST
	SET 	 
    CODE = #{categoryCode}
    WHERE MEMBER_ID = #{memberId} AND LIST_NO = #{listNo}
