mapper.select:
SELECT *
FROM(SELECT ROWNUM INROW, ORDER_DATE, NO, TOTAL_AMOUNT, PREVIEW, PRODUCT_QUANTITY, STATE_CODE, STATE_NAME
FROM(SELECT O.ORDER_DATE, O.NO, O.TOTAL_AMOUNT, O.PREVIEW, O.PRODUCT_QUANTITY, S.CODE STATE_CODE, S.NAME STATE_NAME
	FROM MEMBER_ORDER O JOIN ORDER_STATE S
	ON O.STATE_CODE = S.CODE

	O.MEMBER_ID = #{id}
	AND O.STATE_CODE = ${stateCode}

	ORDER BY O.ORDER_DATE DESC))
WHERE INROW BETWEEN ${begin} AND ${end}

SELECT COUNT(*)
FROM MEMBER_ORDER

MEMBER_ID = #{id}
AND STATE_CODE = ${stateCode}

mapper.update:
UPDATE MEMBER_ORDER
SET STATE_CODE = 3
WHERE NO = ${value}

mapper.select:
SELECT COUNT(*)
FROM MEMBER_ORDER

ORDER_DATE BETWEEN #{beginDate} AND #{endDate}
STATE_CODE = ${stateCode}
AND MEMBER_ID LIKE '%'||#{search}||'%'

SELECT *
FROM(SELECT ROWNUM INROW, ORDER_DATE, MEMBER_ID, NO, TOTAL_AMOUNT, PREVIEW, PRODUCT_QUANTITY, STATE_CODE, STATE_NAME
FROM(SELECT O.ORDER_DATE, O.MEMBER_ID, O.NO, O.TOTAL_AMOUNT, O.PREVIEW, O.PRODUCT_QUANTITY, S.CODE STATE_CODE, S.NAME STATE_NAME
	FROM MEMBER_ORDER O JOIN ORDER_STATE S
	ON O.STATE_CODE = S.CODE

	O.ORDER_DATE BETWEEN #{beginDate} AND #{endDate}
	O.STATE_CODE = ${stateCode}
	AND MEMBER_ID LIKE '%'||#{search}||'%'

	ORDER BY O.ORDER_DATE DESC))
WHERE INROW BETWEEN ${begin} AND ${end}
