mapper.select:
	SELECT PL.NO, PL.CATEGORY_CODE, PL.NAME, PL.PRICE, PL.STOCK
	FROM (
        	SELECT ROWNUM RNUM, P.NO, P.CATEGORY_CODE, P.NAME, P.PRICE, P.STOCK
        	FROM (
                SELECT NO, CATEGORY_CODE, NAME, PRICE, STOCK
				FROM PRODUCT
                
				) P
	) PL
	WHERE PL.RNUM BETWEEN #{start} AND #{end}

SELECT NO, CATEGORY_CODE, NAME, PRICE, STOCK, CONTENT
FROM PRODUCT
WHERE NO = #{no}

mapper.update:
UPDATE PRODUCT
NAME = #{name}, CATEGORY_CODE = #{category_code}, PRICE = #{price}, STOCK = #{stock}, CONTENT = #{content},
WHERE NO = #{no}

mapper.select:
SELECT IDX, STORED_FILE_NAME
FROM TB_FILE
WHERE PARENT_SEQ = #{parentSeq}
